window.addEventListener("scroll",function(){document.querySelector(".navbar").classList.toggle("sticky",window.scrollY>30)});const hamburger=document.querySelector(".hamburger"),navMenu=document.querySelector(".nav-list");hamburger.addEventListener("click",()=>{hamburger.classList.toggle("active"),navMenu.classList.toggle("active")}),document.querySelectorAll(".list-item").forEach(e=>e.addEventListener("click",()=>{hamburger.classList.remove("active"),navMenu.classList.remove("active")}));